function updateBreakDuration({ breakDuration, setBreakDuration, addSub }) {
  switch (breakDuration) {
    case breakDuration < 15 && addSub === "add":
      setBreakDuration(focusDuration + 1);
    case 1 < breakDuration && addSub === "sub":
      setBreakDuration(focusDuration - 1);
  }
}
